mapper.select:
select
id, student_id, grade, speciality, faculty, name, sex, class_name, dept_id, del_flag, create_by, create_time,
update_by, update_time
from sys_apply_form


        and id = #{id}


        and student_id = #{studentId}


        and grade = #{grade}


        and speciality = #{speciality}


        and faculty = #{faculty}


        and name = #{name}


        and sex = #{sex}


        and class_name = #{className}


        and dept_id = #{deptId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_apply_form


        and id = #{id}


        and student_id = #{studentId}


        and grade = #{grade}


        and speciality = #{speciality}


        and faculty = #{faculty}


        and name = #{name}


        and sex = #{sex}


        and class_name = #{className}


        and dept_id = #{deptId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}

mapper.update:
update sys_apply_form
set del_flag = '2'
where id = #{id}
