mapper.select:
select
id, name, content, image_url, is_link, external_link, introduction, del_flag,
create_by, create_time, update_by, update_time
from sys_dept


    del_flag = '0'

        and id = #{sysDept.id}


        and name = #{sysDept.name}


        and content = #{sysDept.content}


        and is_link = #{sysDept.isLink}


        and external_link = #{sysDept.externalLink}


        and introduction = #{sysDept.introduction}


        and del_flag = #{sysDept.delFlag}


        and create_by = #{sysDept.createBy}


        and create_time = #{sysDept.createTime}


        and update_by = #{sysDept.updateBy}


        and update_time = #{sysDept.updateTime}


limit #{offset}, #{limit}

select count(1)
from sys_dept


        and id = #{id}


        and name = #{name}


        and content = #{content}


        and is_link = #{isLink}


        and external_link = #{externalLink}


        and introduction = #{introduction}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}

mapper.insert:
insert into sys_dept(
    name, content, image_url, is_link, external_link, introduction,
    del_flag, create_by, create_time, update_by, update_time
)
values (
           #{name}, #{content}, #{imageUrl}, #{isLink}, #{externalLink}, #{introduction},
           #{delFlag}, #{createBy}, #{createTime}, #{updateBy}, #{updateTime}
       )

insert into sys_dept(
name, content, image_url, is_link, external_link, introduction,
del_flag, create_by, create_time, update_by, update_time
)
values

    (
    #{entity.name}, #{entity.content}, #{entity.imageUrl}, #{entity.isLink}, #{entity.externalLink}, #{entity.introduction},
    #{entity.delFlag}, #{entity.createBy}, #{entity.createTime}, #{entity.updateBy}, #{entity.updateTime}
    )
