mapper.select:
select
id, apply_id, state, remark, del_flag, create_by, create_time, update_by, update_time
from sys_review


        and id = #{id}


        and apply_id = #{applyId}


        and state = #{state}


        and remark = #{remark}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_review


        and id = #{id}


        and apply_id = #{applyId}


        and state = #{state}


        and remark = #{remark}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}

select id, apply_id, state, remark, del_flag, create_by, create_time, update_by, update_time from sys_review
apply_id = #{applyId} and del_flag = '0'
and state = #{status}

mapper.update:
update sys_review
set del_flag = '2'
where id = #{id}
