mapper.select:
select
user_id, role_id, del_flag, create_by, create_time, update_by, update_time
from sys_user_role


        and user_id = #{userId}


        and role_id = #{roleId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_user_role


        and user_id = #{userId}


        and role_id = #{roleId}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
