mapper.select:
SELECT id, title, brief, introduction, start_time, picture, del_flag, create_by, create_time, update_by, update_time FROM ws_activity
del_flag = '0'
AND title LIKE CONCAT('%', #{query.title}, '%')
AND brief LIKE CONCAT('%', #{query.brief}, '%')
AND start_time BETWEEN #{query.startTime} AND #{query.endTime}
ORDER BY start_time DESC, create_time DESC LIMIT #{offset}, #{limit}

select count(1) from ws_activity
del_flag = '0'
and id = #{id}
and title like concat('%', #{title}, '%')
and brief like concat('%', #{brief}, '%')
and start_time = #{startTime}

mapper.delete:
update ws_activity
set del_flag = '2'
where id = #{id}
